# PUT
curl -k -X PUT -d "" http://localhost:8000/put_test/file -i; # 0
curl -k -X PUT -d "1" http://localhost:8000/put_test/file -i; # 1
curl -k -X PUT -d "" http://localhost:8000/put_test/file -i; # 4096

curl -k -H "Transfer-Encoding: chunked" -X PUT -d "" http://localhost:8000/put_test/file -i; # 0
curl -k -H "Transfer-Encoding: chunked" -X PUT -d "1" http://localhost:8000/put_test/file -i; # 1
curl -k -H "Transfer-Encoding: chunked" -X PUT -d "" http://localhost:8000/put_test/file -i; # 4096

